changeSet.sql:
CREATE OR REPLACE FUNCTION preset_balance() RETURNS TRIGGER AS $$
DECLARE
    ctbalance INTEGER;
    dtbalance INTEGER;
BEGIN
    SELECT balance(NEW.ct) INTO ctbalance;
    SELECT balance(NEW.dt) INTO dtbalance;
    NEW.ctbalance := ctbalance - NEW.amount;
    NEW.dtbalance := dtbalance + NEW.amount;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql

DO language plpgsql $$
DECLARE
    balance INTEGER;
BEGIN
    TRUNCATE receipt CASCADE;
    INSERT INTO receipt (time, ct, ctrule, dt, dtrule, amount, details)
        VALUES (TIMESTAMP '2010-05-16T15:36:38Z', 'urn:test:1', 'alpha', 'urn:test:2', 'foo', 10, 'test line 1');
    INSERT INTO receipt (time, ct, ctrule, dt, dtrule, amount, details)
        VALUES (TIMESTAMP '2010-05-16T15:36:38Z', 'urn:test:1', 'alpha', 'urn:test:2', 'foo', 10, 'test line 2');
    INSERT INTO receipt (time, ct, ctrule, dt, dtrule, amount, details)
        VALUES (NOW() + '1 DAY'::INTERVAL, 'urn:test:2', 'beta', 'urn:test:1', 'foo', 55000, 'big amount, should stay');
    SELECT balance('urn:test:1') INTO balance;
    IF balance != 54080 THEN
        RAISE EXCEPTION 'Balance of 54080 expected, % found', balance;
    END IF;
    SELECT balance('urn:test:2') INTO balance;
    IF balance != -54080 THEN
        RAISE EXCEPTION 'Balance of 54080 expected, % found', balance;
    END IF;
END
$$;
